	Если Не Найти(Строка.Реквизит, "АдресМестаДоставки") = 0 Тогда
		АдресМестаДоставкиСтруктура.Вставить(СтрЗаменить(Строка.Реквизит, "АдресМестаДоставки", ""), Строка.Значение);
		АдресМестаДоставки = АдресМестаДоставки + Строка.Значение + ", ";

		Если Строка.Реквизит = "АдресМестаДоставкиИндекс" Тогда	
			АдресМестаДоставкиСтруктура.Вставить("ТипАдреса", "АдрРФ");
