			АдресМестаДоставкиСтруктура.Вставить("ТипАдреса", "АдрСтр");
		ИначеЕсли Строка.Реквизит = "АдресМестаДоставкиКодГАР" Тогда
			АдресМестаДоставкиСтруктура.Вставить("ТипАдреса", "КодГАР");
		КонецЕсли;
	КонецЕсли;
	Если Не Найти(Строка.Реквизит, "АдресМестаПогрузки") = 0 Тогда
	    АдресМестаПогрузкиСтруктура.Вставить(СтрЗаменить(Строка.Реквизит, "АдресМестаПогрузки", ""), Строка.Значение);	
	    АдресМестаПогрузки = АдресМестаПогрузки + Строка.Значение + ", ";

	    Если Строка.Реквизит = "АдресМестаПогрузкиИндекс" Тогда	
			АдресМестаПогрузкиСтруктура.Вставить("ТипАдреса", "АдрРФ");
		ИначеЕсли Строка.Реквизит = "АдресМестаПогрузкиКодСтр" Тогда
			АдресМестаПогрузкиСтруктура.Вставить("ТипАдреса", "АдрСтр");
		ИначеЕсли Строка.Реквизит = "АдресМестаПогрузкиКодГАР" Тогда
			АдресМестаПогрузкиСтруктура.Вставить("ТипАдреса", "КодГАР");
		КонецЕсли;
	КонецЕсли;
