	Для Каждого Колонка Из ТЗГруз.Колонки Цикл
		МассивЗначений = DR_СтрРазделить(Колонка.Имя,"_");						
		Колонка.Имя = МассивЗначений[МассивЗначений.Количество()-1];

		Если Колонка.Имя = "КолМест" Тогда
			Колонка.Имя = "КолМестГр";
		КонецЕсли;	
